import logo from "./logo.svg";
import "./App.css";
import { useState, useEffect } from "react";
import axios from "axios";
import Posts from "./components/Posts";
import Pagination from "./components/Pagination";

function App() {
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      const response = await axios.get("http://localhost:8080/api/page", {
        params: {
          page: currentPage,
          size: postsPerPage,
        },
      });
      // setIndexOfFirstPage(response.data.first);
      console.log(response.data.first);
      // setPosts(response.data);
    };
    fetchData();
  }, []);
  console.log(posts);
  // 페이지 네이션을 위한 필요 데이터
  /* 
  1. 페이지당 출력 개수(10) postPerPage
  2. indexOfLastPage
  3. indexOfFirstPage
  4. currentPage
*/

  const [currentPage, setCurrentPage] = useState(1);
  const [postsPerPage, setPostsPerPage] = useState(10);
  const [indexOfLastPage, setIndexOfLastPage] = useState("");
  const [indexOfFirstPage, setIndexOfFirstPage] = useState("");
  const currentPosts = (posts) => {
    let currentPosts = [];
    currentPosts = posts.slice(indexOfFirstPage, indexOfLastPage);
    return currentPosts;
  };
  console.log(currentPage);
  return (
    <div className="App">
      <Posts posts={currentPosts(posts)}> </Posts>
      <Pagination
        postsPerPage={postsPerPage}
        totalPosts={posts.length}
        setCurrentPage={setCurrentPage}
      ></Pagination>
    </div>
  );
}

export default App;
