import logo from "./logo.svg";
import "./App.css";
import { useState, useEffect } from "react";
import axios from "axios";
import Posts from "./components/Posts";
import Pagination from "./components/Pagination";

function App() {
  const [posts, setPosts] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  useEffect(() => {
    const fetchData = async () => {
      const response = await axios.get("http://localhost:8080/api/page", {
        params: {
          page: currentPage,
          size: size,
        },
      });
      setPosts(response.data.dtoList);
      setTotalPage(response.data.totalPage);
      setCurrentPage(response.data.page);
      setSize(response.data.size);
      setIndexOfFirstPage(response.data.start);
      setIndexOfLastPage(response.data.end);
      setPageList(response.data.pageList);
      setPrev(response.data.prev);
      setNext(response.data.next);
    };
    fetchData();
  }, [currentPage]);
  console.log(posts);
  // 페이지 네이션을 위한 필요 데이터
  /* 
  1. 페이지당 출력 개수(10) postPerPage
  2. indexOfLastPage
  3. indexOfFirstPage
  4. currentPage
*/
  const [size, setSize] = useState(10);
  const [totalPage, setTotalPage] = useState(11);
  const [indexOfLastPage, setIndexOfLastPage] = useState("");
  const [indexOfFirstPage, setIndexOfFirstPage] = useState("");
  const [prev, setPrev] = useState(false);
  const [next, setNext] = useState(false);
  // const currentPosts = (posts) => {

  //   currentPosts = posts.slice(indexOfFirstPage, indexOfLastPage);
  //   return currentPosts;
  // };
  const [pageList, setPageList] = useState([]);
  return (
    <div className="App">
      <Posts posts={posts}> </Posts>
      <Pagination
        prev={prev}
        next={next}
        pageNumbers={pageList}
        setCurrentPage={setCurrentPage}
        currentPage={currentPage}
      ></Pagination>
    </div>
  );
}

export default App;
